function voteClicked() {
      var voteOptions = document.getElementsByClassName('vote-option');

      // Loop through vote options and remove them
      while (voteOptions.length > 0) {
        voteOptions[0].remove();
      }

      // Hide the vote button
      var voteButton = document.getElementById('voteButton');
      voteButton.style.display = 'none';

      // Display a message or perform any other actions after voting
      console.log("Vote submitted! Thank you.");}